    private void BuscaInfoProduto(int nProduto) {


        String a = ",";
        String[] array = dados[nProduto].split(a);

        guia = Integer.parseInt(array[0]);
        nclasse = array[1];
        nrisco = array[2];

    }

    private void MostraInfoProduto(int i) {

        nrONU.setText(r.getString(R.string.dica_nr_onu, onu[i]));
        nomeProduto.setText(Html.fromHtml("<b>" + r.getString(R.string.dica_nome_produto) + "</b>" + "<br />" + produto[i]));

        if (nrisco.equals("0")) {
            nrRisco.setVisibility(View.GONE);
            nomeRisco.setText(Html.fromHtml("<b>" + r.getString(R.string.dica_nome_risco) + "</b>" + "<br />" + nomeGuia[guia]));
        } else {
            nrRisco.setText(r.getString(R.string.dica_nr_risco, nrisco));
            int j = 0;
            while (!nrisco.equals(risco[j]) && j < risco.length) {
                j++;
            }
            nomeRisco.setText(Html.fromHtml("<b>" + r.getString(R.string.dica_nome_risco) + "</b>" + "<br />" + detalheRisco[j]));
        }

        nrClasse.setText(r.getString(R.string.dica_nr_classe, nclasse));

        saude.setText(Html.fromHtml("<b>" + r.getString(R.string.dica_saude) + "</b>" + "<br />" + riscoSaude[guia]));
        incendio.setText(Html.fromHtml("<b>" + r.getString(R.string.dica_incendio) + "</b>" + "<br />" + riscoIncendio[guia]));
        nivelProtecao.setText(Html.fromHtml("<b>" + r.getString(R.string.dica_protecao) + "</b>" + "<br />" + epi[guia]));
        isolamento.setText(Html.fromHtml("<b>" + r.getString(R.string.dica_isolamento) + "</b>" + "<br />" + isola[guia]));
        evacuacao.setText(Html.fromHtml("<b>" + r.getString(R.string.dica_evacuacao) + "</b>" + "<br />" + evacua[guia]));
    }


